    public void showCF() {
        Label label = new Label("Konversi Celcius ke Fahrenheit");
        label.setFont(Font.font("Cambria", 20));

        Label label2 = new Label("Masukkan angka :");
        TextField input = new TextField();
        
        Label output = new Label();

        Button konversi = new Button("Konversi");
        konversi.setOnAction(action -> {
            try {
                double celcius = Double.parseDouble(input.getText());
                double hasil = celcius * 9/5 + 32;
                output.setText("Hasil konversi Celcius ke Fahrenheit : " + hasil);
            } catch (NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        Button clear = new Button("Clear");
        clear.setStyle("-fx-background-color: #B0C4DE");
        clear.setOnAction(action -> {
            input.setText("");
            output.setText("");
        });

        Button back = new Button("Back to Konversi Suhu");
        back.setStyle("-fx-background-color: #8FBC8F");
        back.setOnAction(action -> {
            showKSuhuScene();
        });

        VBox vbox = new VBox(label, label2, input, output, konversi, clear, back);
        vbox.setAlignment(Pos.CENTER);
        vbox.setSpacing(5);

        Scene scene = new Scene(vbox, 300, 400);
        stage.setScene(scene);
    }

