    public void showCalTrigonometri() {
        Label label1 = new Label("Trigonometri");
        label1.setFont(Font.font("Cambria", 25));

        Label label2 = new Label("Masukkan angka :");
        TextField input = new TextField();

        Label output = new Label();

        Button sin = new Button("Sin");
        sin.setOnAction(action -> {
            try{
                double derajat = Double.parseDouble(input.getText());
                double radian = Math.toRadians(derajat);
                double hasil = Math.sin(radian);
                output.setText("Hasil sin = " + hasil);
            } catch(NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        Button cos = new Button("Cos");
        cos.setOnAction(action -> {
            try {
                double derajat = Double.parseDouble(input.getText());
                double radian = Math.toRadians(derajat);
                double hasil = Math.cos(radian);
                output.setText("Hasil  cos = " + hasil);
            } catch(NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        Button tan = new Button("Tan");
        tan.setOnAction(action -> {
            try {
                double derajat = Double.parseDouble(input.getText());
                double radian = Math.toRadians(derajat);
                double hasil = Math.tan(radian);
                output.setText("Hasil tan = " + hasil);
            } catch(NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        HBox sinCosTan = new HBox(sin, cos, tan);
        sinCosTan.setAlignment(Pos.CENTER);
        sinCosTan.setSpacing(5);

        Button clear = new Button("Clear");
        clear.setStyle("-fx-background-color: #B0C4DE");
        clear.setOnAction(action -> {
            input.setText("");
            output.setText("");
        });

        Button back = new Button("Back to Pilih Menu");
        back.setStyle("-fx-background-color: #8FBC8F");
        back.setOnAction(action -> {
            showScene2();
        });

        VBox vbox = new VBox(label1, label2, input, output, sinCosTan, clear, back);
        vbox.setAlignment(Pos.CENTER);
        vbox.setSpacing(15);

        Scene scene = new Scene(vbox, 300, 400);
        stage.setScene(scene);
    }

    public void showCalBasicArtihmetic() {
        Label label = new Label("Aritmatik Dasar");
        label.setFont(Font.font("Cambria", 25));

        TextField input1 = new TextField();
        input1.setPromptText("Input angka");

        TextField input2 = new TextField();
        input2.setPromptText("Input angka");

        Label output = new Label();

        Button kali = new Button("x");
        kali.setOnAction(action -> {
            try {
                double angka1 = Double.parseDouble(input1.getText());
                double angka2 = Double.parseDouble(input2.getText());
                double hasil = angka1 * angka2;
                output.setText("Hasil kali = " + hasil);
            } catch(NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        Button bagi = new Button("/");
        bagi.setOnAction(action -> {
            try {
                double angka1 = Double.parseDouble(input1.getText());
                double angka2 = Double.parseDouble(input2.getText());
                double hasil = (double) angka1 / angka2;
                output.setText("Hasil bagi = " + hasil);
            } catch(NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        HBox kaliBagi = new HBox(kali, bagi);
        kaliBagi.setAlignment(Pos.CENTER);
        kaliBagi.setSpacing(10);

        Button tambah = new Button("+");
        tambah.setOnAction(action -> {
            try {
                double angka1 = Double.parseDouble(input1.getText());
                double angka2 = Double.parseDouble(input2.getText());
                double hasil = angka1 + angka2;
                output.setText("Hasil tambah = " + hasil);
            } catch(NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        Button kurang = new Button("-");
        kurang.setOnAction(action -> {
            try {
                double angka1 = Double.parseDouble(input1.getText());
                double angka2 = Double.parseDouble(input2.getText());
                double hasil = angka1 - angka2;
                output.setText("Hasil kurang = " + hasil);
            } catch(NumberFormatException e) {
                output.setText("Inputan bukan angka");
            }
        });

        HBox tambahKurang = new HBox(tambah, kurang);
        tambahKurang.setAlignment(Pos.CENTER);
        tambahKurang.setSpacing(10);

        VBox kaliBaTaKu = new VBox(kaliBagi, tambahKurang);
        kaliBaTaKu.setAlignment(Pos.CENTER);
        kaliBaTaKu.setSpacing(10);

        Button clear = new Button("Clear");
        clear.setStyle("-fx-background-color: #B0C4DE");
        clear.setOnAction(action -> {
            input1.setText("");
            input2.setText("");
            output.setText("");
        });

        Button back = new Button("Back to Pilih Menu");
        back.setStyle("-fx-background-color: #8FBC8F");
        back.setOnAction(action -> {
            showScene2();
        });

        VBox vbox = new VBox(label, input1, input2, output, kaliBaTaKu, clear, back);
        vbox.setAlignment(Pos.CENTER);
        vbox.setSpacing(15);

        Scene scene = new Scene(vbox, 300, 400);
        stage.setScene(scene);
    }

